installer-gui-script.script:
function installationCheck() {

    var minOSVersion = '12.4';

    if (system.compareVersions(system.version.ProductVersion, minOSVersion) < 0) {
        my.result.title = 'Incompatible macOS Version';
        my.result.message = 'This program require macOS ' + minOSVersion + ' or higher version.';
        my.result.type = 'Fatal';
        return false;
    }
    return true;
}
